import './productos.css';
import React from 'react'
import Results from './Results'
import Filtros from './Filtros'
import Paginador from './Paginador'

// Hooks, roots, etc
import { useState } from "react";

//imagenes
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Productos({productsItems}){

    let [productList, setProductList] = useState(productsItems);
    let [categoryFilter, setCategoryFilter] = useState(productsItems);


    return (
        <div className="productos">
            <div className="portada"></div>
            <div className="filter-container">
               <form>
                    <Filtros
                    productsItems={productsItems}
                    filter={category}
                    />
                    <button type="reset" onClick={btnReset}>
                        <FontAwesomeIcon icon={faTrashAlt} />
                        Borrar filtros
                    </button>
                </form>
            </div>
            <Results
                productList={productList}
            />
            <Paginador
            productList={productList}
             />
        </div>
    );



    function category(e) {

        setProductList(e.splice(0,16));
    };


    function btnReset() {
        setProductList(productsItems);
    };
}

export default Productos;
