import './item.css';
import React from 'react'
import { Route, Link, useParams } from 'react-router-dom';
import coin from '../images/icons/coin.svg'


function Item(){
    const {id} = useParams();

    let [productsItems, setProductsItems] = useState([])

    useEffect(()=>{
        let api = fetch("https://coding-challenge-api.aerolab.co/products",{
            headers: {
                "Content-type": "application/json",
                "Accept" : "application/json",
                "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZWRkOWU5OTQ0NGZlNDAwNmRhOTkyNGQiLCJpYXQiOjE1OTE1ODIzNjF9.-f40dyUIGFsBSB_PTeBGdSLI58I21-QBJNi9wkODcKk",
            }
        });
        api 
        .then((response) => {
        return response.json();
        })
        .then((results) => {
            console.log(results)
            setProductsItems(results);
        })
    },[]);

    return(
        <section className="item">
            <div className="item-container">
                <div className="picture">
                    <img src={productsItems} alt=""/>    
                </div>
                <div className="info-container">
                    <h4>{productsItems.category}</h4>
                    <h2>{productsItems.name}</h2>
                    <h5>Valor: <img src={coin} alt="coin"/><span>precio variable</span></h5>
                    <div>
                        <button>Canjear
                        <img src="" alt="Buy"/>
                        </button>
                    </div>
                </div>
            </div>
            <div className="similar-container" >
                  <h5 className="similar-title">Otros productos similares</h5> 
                  <div className="product-container">
                {/* for each productos hasta 4 */} 
                <a className="each-product" href="#dinamic" target="_self">
                    <div className="image-container">
                        <div src="" alt="#product-name" className="image-product"/>
                        <div src="icon ? disponible:a-partir-x-coins" alt="" className="available-price"/>
                    </div>
                    <h5 className="category">Categoria</h5>
                    <h6 className="name-product">Producto</h6>
                </a>
                {/* FIN for each productos */} 
            </div> 
            </div>
        </section>
    );
}

export default Item;
