import './creditos.css';
import React, { Component } from 'react';
import coin from '../images/icons/coin.svg'
import { useState, useEffect} from "react";


function Creditos(){

    const [postCredit, setPostCredit] = useState()

    useEffect(() =>{
        let api = fetch("https://coding-challenge-api.aerolab.co/user/points",{
          headers: {
            "Content-type": "application/json",
            "Accept" : "application/json",
            "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGIwNTI3MjliNzc4MTAwMjA5YzVhYWIiLCJpYXQiOjE2MjIxNjgxNzh9.rvgBvfaLqxHZv-gU_GUyFz4c6hIybWT9rihd6MFTnno",
          }, 
          method:'POST',
          body:JSON.stringify(this.postCredit)
        });
        api 
        .then((response) => {
          return response.json();
        })
      });


    function obtenerValor(event){
        console.log("prueba de obtener valor" + event)
    }


    return(
        <section className="credito">
            <div className="container-credit">
                <div className="text-container">
                    <h3>Te quedaste sin puntos</h3>
                    <h5>Ahora podés sumar cargando tu nombre de usuario y contraseña</h5>
                    <p>Recuerda que sólo podrás cargar los valores que están prestablecidos</p>
                </div>
                <div className="form-container">
                    <form onSubmit={postCredit}>
                        <input className="input user" placeholder="Usuario" type="text"/>
                        <input className="input pass" placeholder="Contraseña" type="text"/>
                        <div className="btn-container">
                            <input type="radio" id="1000" name="credit" value="1000" onChange={this.obtenerValor}/>
                            <label for="1000">1000</label>
                            <input type="radio" id="5000" name="credit" value="5000" />
                            <label for="5000">5000</label>
                            <input type="radio" id="7500" name="credit" value="7500" />
                            <label for="7500">7500</label>
                        </div>
                        <button>Canjear</button>
                    </form>
                </div>

            </div>
        </section>
    );
}

export default Creditos;
