import './productos.css';
import React from 'react'
import ProductCard from './ProductCard'

// Hooks, roots, etc
import { Link} from 'react-router-dom';
import { useState, useEffect} from "react";

//imagenes
import next from '../images/icons/arrow-right.svg'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Productos({productsItems, categoryProducts, cheapOrder}){

    // filtra las categorias para ponerlas en el select
    var options = {};
    categoryProducts = categoryProducts.filter(function(current) {
    var exists = !options[current.category];
    options[current.category] = true;
    return exists;
    });

    //filtra de menor a mayor
    cheapOrder.sort(function(a, b) {
        return a - b;
      });
      console.log(cheapOrder.cost)

    //filtra de mayor a menor



    return (
        <div className="productos">
            <div className="portada"></div>
            <div className="filter-container">
               {/* filtros */}
               <form action="">
                    <select name="Categorias" id="categories">
                        {categoryProducts.map((option)=>
                            <option key={option._id} value={option.category}>{option.category}</option>
                        )}
                    </select>
                    <button>Más bajo primero</button>
                    <button >Más alto primero</button>
                    <button>
                        <FontAwesomeIcon icon={faTrashAlt} />
                        Borrar filtros
                    </button>
                </form>
            </div>
            <div className="product-container">
                {productsItems.slice(0, 16).map((product) =>
                <Link to ={`producto/${product._id}`} style={{ textDecoration: 'none' }}>
                    <ProductCard 
                    product={product}
                    {...product}/>
                </Link>
                )}
            </div>
            {/* paginador */} 
            <a className="pagination" href="" target="_self">
                Siguiente
                <img src={next} alt="next"/>
            </a>
            {/* paginador */} 
        </div>
    );
}

export default Productos;
