// import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import Productos from './productos/Productos'
import Header from './productos/Header'
import Creditos from './mas-creditos/Creditos'
import Historial from './historial/Historial'
import Item from './item/Item'
import { Route, Link } from 'react-router-dom';
import coin from './images/icons/coin.svg';
// get our fontawesome imports
//import { faGithub } from "@fortawesome/free-solid-svg-icons";
//import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

let [userData, setUserData] = useState({})

useEffect(()=>{
  let api = fetch("https://coding-challenge-api.aerolab.co/user/me",{
    headers: {
      "Content-type": "application/json",
      "Accept" : "application/json",
      "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZWRkOWU5OTQ0NGZlNDAwNmRhOTkyNGQiLCJpYXQiOjE1OTE1ODIzNjF9.-f40dyUIGFsBSB_PTeBGdSLI58I21-QBJNi9wkODcKk",
    }
  });
  api
  .then((response)=>response.json())
  .then((result)=> {
    console.log(result);
    setUserData(result);
  }
  )
})

fetch("https://coding-challenge-api.aerolab.co/user/me",{
  headers: {
    "Content-type": "application/json",
    "Accept" : "application/json",
    "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZWRkOWU5OTQ0NGZlNDAwNmRhOTkyNGQiLCJpYXQiOjE1OTE1ODIzNjF9.-f40dyUIGFsBSB_PTeBGdSLI58I21-QBJNi9wkODcKk",
  }
})
.then((response)=>response.json())
.then((json)=>
console.log(json)

);


function App() {
  return (
    <div className="App">
      <Header />
      <header>
        <div className="max-1440">
          <img src="" alt="Logo"/>
          <nav className="principal-header">
            <ul>
              <li>
                <Link to="/">Inicio</Link>
                </li> 
                <li>
                <Link to="/mas-creditos">Más Creditos</Link>
                </li> 
                <li>
                <Link to="/historial">Historial</Link>
                </li>
                <li>
                <Link to="/producto">producto especifico</Link>
                </li> 
            </ul>
            <div className="profile">
              <h4>{userData.name}</h4>
              <img src="" alt=""/>
              <div className="coins-amount">
                <img src={coin} alt="Coin"/>
                <p>{userData.points}</p>
              </div>
            </div>
          </nav>  
        </div>
      </header>

        <Route exact path="/">
          <Productos/>
        </Route>
        <Route exact path="/mas-creditos">
          <Creditos />
        </Route>
        <Route exact path="/historial">
          <Historial />
        </Route>
        <Route exact path="/producto">
          <Item />
        </Route>
      <footer>
        <div className="footer-1440">
          <p>Hecho por Tomás Sánchez Soria</p>
          <a href="https://twitter.com/tomi_SS" target="_blank">
            <img src="" alt="Twitter"/>
          </a>
          <a href="https://www.linkedin.com/in/sanchezsoria/" target="_blank">
            <img src="" alt="LinkedIn"/>
          </a>
          <a href="https://github.com/tosaso/" target="_blank">
            <img src="" alt="Github"/>
          </a>
        </div>
      </footer>
    </div>
  );
}

export default App;
