import './productos.css';
import React from 'react'
import ProductCard from './ProductCard'
import Filtros from './Filtros'

// Hooks, roots, etc
import { Link} from 'react-router-dom';
import { useState } from "react";

//imagenes
import next from '../images/icons/arrow-right.svg'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Productos({productsItems, categoryProducts}){

    let [categoryFilter, setCategoryFilter] = useState("Todas");
    let [cheaperFilter, setCheaperFilter] = useState(false);
    let [expensiveFilter, setExpensiveFilter] = useState(false);
    let [productList, setProductList] = useState(productsItems);


    return (
        <div className="productos">
            <div className="portada"></div>
            <div className="filter-container">
               {/* filtros */}
               <form action="">
                    <Filtros />
                    <button type="reset" onClick={btnReset}>
                        <FontAwesomeIcon icon={faTrashAlt} />
                        Borrar filtros
                    </button>
                </form>
            </div>

            {/* paginador */} 
            <a className="pagination" href="" target="_self">
                Siguiente
                <img src={next} alt="next"/>
            </a>
            {/* paginador */} 
        </div>
    );

    function category (ultimaLista, state){
        setCategoryFilter(state);
        setProductList(ultimaLista);
    };

    // funcion para filtar habitaciones


    function filterCategory(e) {
        let updatelist = productsItems
        .filter((value) => {
            return e.target.value === "Todos"
            ? value
            : categoryProducts(value.category) === e.target.value;
        });
        filterCategory(updatelist, e.target.value);


 };
 function btnReset() {
    setProductList(productsItems);
    setExpensiveFilter(false);
    setCheaperFilter(false);
    setCategoryFilter("Todas");
};
}

export default Productos;
