import './productos.css';
import React from 'react'
import ProductCard from './ProductCard'

// Hooks, roots, etc
import { Link} from 'react-router-dom';
import { useState } from "react";

//imagenes
import next from '../images/icons/arrow-right.svg'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Productos({productsItems, categoryProducts, cheapOrder}){

    // filtra las categorias para ponerlas en el select
    var options = {};
    categoryProducts = categoryProducts.filter(function(current) {
    var exists = !options[current.category];
    options[current.category] = true;
    return exists;
    });

    let [categoryFilter, setCategoryFilter] = useState("Todas");
    let [cheaperFilter, setCheaperFilter] = useState(false);
    let [expensiveFilter, setExpensiveFilter] = useState(false);
    let [productList, setProductList] = useState(productsItems);

    //filtra de menor a mayor
        //   function cheapOrder(e) {
    //     cheapOrder.sort(function(a, b) {
    //         if (a.cost > b.cost) {
    //             return 1;
    //           }
    //           if (a.cost < b.cost) {
    //             return -1;
    //           }
    //           // a must be equal to b
    //           return 0;
    //       });
    //   }

    return (
        <div className="productos">
            <div className="portada"></div>
            <div className="filter-container">
               {/* filtros */}
               <form action="">
                    <select name="Categorias" id="categories" onChange={filterCategory}>
                        {categoryProducts.map((option)=>
                            <option key={option._id} value={option.category}>{option.category}</option>
                        )}
                    </select>
                    <button onChange={cheapOrder}>Más bajo primero</button>
                    <button >Más alto primero</button>
                    <button type="reset" onClick={btnReset}>
                        <FontAwesomeIcon icon={faTrashAlt} />
                        Borrar filtros
                    </button>
                </form>
            </div>
            <div className="product-container">
                {productsItems.slice(0, 16).map((product) =>
                <Link to ={`producto/${product._id}`} style={{ textDecoration: 'none' }}>
                    <ProductCard 
                    product={product}
                    {...product}/>
                </Link>
                )}
            </div>
            {/* paginador */} 
            <a className="pagination" href="" target="_self">
                Siguiente
                <img src={next} alt="next"/>
            </a>
            {/* paginador */} 
        </div>
    );

    function category (ultimaLista, state){
        setCategoryFilter(state);
        setProductList(ultimaLista);
    };

    // funcion para filtar habitaciones


    function filterCategory(e) {
        let updatelist = productsItems
        .filter((value) => {
            return e.target.value === "Todos"
            ? value
            : categoryProducts(value.category) === e.target.value;
        });
        filterCategory(updatelist, e.target.value);


 };
 function btnReset() {
    setProductList(productsItems);
    setExpensiveFilter(false);
    setCheaperFilter(false);
    setCategoryFilter("Todas");
};
}

export default Productos;
