import './productos.css';
import React from 'react'
import Results from './Results'
import Filtros from './Filtros'
import Paginador from './Paginador'

// Hooks, roots, etc
import { useState } from "react";

//imagenes
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Productos({productsItems}){

    let [productList, setProductList] = useState(productsItems);


    return (
        <div className="productos">
            <div className="portada"></div>
            <div className="filter-container">
               <form>
                    <Filtros
                    productsItems={productsItems}
                    category={category}
                    />
                    <button type="reset" onClick={btnReset}>
                        <FontAwesomeIcon icon={faTrashAlt} />
                        Borrar filtros
                    </button>
                </form>
            </div>
            <Results
                productList={productList}
            />
            <Paginador
            productList={productList}
             />
        </div>
    );

    function filterArticle(selected){
        setItems(selected.splice(0,16)); 
    }


    function category(ultimaLista, state) {
        setCategoryFilter(state);
        setProductList(ultimaLista);
    };

    // function highPrice(ultimaLista, state) {
    //     setHigherPrice(state);
    //     setProductList(ultimaLista);
    // };

    function btnReset() {
        setProductList(productsItems);
        setCategoryFilter("Todas");
    };
}

export default Productos;
